
"use strict";var navToggle=document.querySelector('.header__btn-toggle');var header=document.querySelector('.header');var popupBtn=document.querySelector('.popup-btn');var closeBtn=document.querySelector('.popup-close');var popup=document.querySelector('.rates-popup');var filterBtn=document.querySelector('.country-filters__toggle');var filterCloseBtn=document.querySelector('.country-filters__btn');var filter=document.querySelector('.country-filters');var filterTitle=document.querySelector('.country-filters__title');var filterFormItems=document.querySelectorAll('.search-mate__item');var windowWidth=window.innerWidth;var tabletWidth=768;var desktopWidth=1024;header.classList.remove('header--nojs');navToggle.addEventListener('click',function(){header.classList.toggle('header--closed');header.classList.toggle('header--opened');});if(popupBtn){popupBtn.addEventListener('click',function(evt){evt.preventDefault();popup.classList.add('popup-show');});}
